def fget_color_val(index, type='rgb'):
    ''' for COLORS_RGB '''
    assert index < len(COLORS_RGB)
    color_rgb = (COLORS_RGB[index] * 255).astype(np.uint8).tolist()
    if type == 'hex':
        return frgb2hex(color_rgb)
    return color_rgb


def fget_color_random(type='rgb'):
    ''' for COLORS_RGB '''
    randint = random.randint(0, len(COLORS_RGB) - 1)
    if type == 'hex':
        return frgb2hex(fget_color_val(randint))
    return fget_color_val(randint)


def fcre_color_indexes(num, data_type='rgb'):
    ''' for COLORS_RGB '''
    if data_type == 'map':
        assert num <= len(COLORS_MAP)
        return random.sample(range(0, len(COLORS_MAP)), num)
    assert num <= len(COLORS_RGB), '出错 num = ' % num
    return random.sample(range(0, len(COLORS_RGB)), num)


def fhex2rgb(hex):
    if hex.startswith('#'):
        hex = hex[1:]
    r = int(hex[0:2], 16)
    g = int(hex[2:4], 16)
    b = int(hex[4:6], 16)
    #     rgb = str(r)+','+str(g)+','+str(b)
    rgb = [r, g, b]
    #     print(rgb)
    return rgb


def frgb2hex(rgb):
    # RGB = rgb.split(',')
    color = '#'
    for i in rgb:
        num = int(i)
        color += str(hex(num))[-2:].replace('x', '0').upper()
    #     print(color)
    return color


